import React from "react";

const Modal = ({ isOpen, onClose, children }) => {
    const modalStyles = {
      // Add your styles here
      // You can use the provided className or customize it further
      // For brevity, I'll just include the provided className in the style attribute
      // You might want to split this into a separate CSS module or use a styling solution like Styled Components or Emotion for better maintainability.
      className: "group relative ring-2 ring-base-300 bg-base-200 rounded-2xl shadow-xl z-index-0",
      position: 'fixed',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      padding: '20px',
      minWidth: '300px',
      maxWidth: '80%',
      overflowY: 'auto',
      maxHeight: '80%',
      display: isOpen ? 'block' : 'none',
    };
  
    return (
      <div style={modalStyles}>
        {children}
        <button onClick={onClose}>Close Modal</button>
      </div>
    );
  };
