import React from "react";

const Modal = ({ isOpen, onClose}) => {
    const modalStyles = {
        className: "group relative ring-2 ring-base-300 bg-custom-color rounded-2xl shadow-xl z-index-10",
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        padding: '20px',
        minWidth: '300px',
        maxWidth: '80%',
        overflowY: 'auto',
        maxHeight: '80%',
        display: isOpen ? 'block' : 'none',
        // Add a custom background color
        backgroundColor: '#3498db', // You can replace this with your desired color
      };
  
    return (
      <div style={modalStyles}>
        <button onClick={onClose}>Close Modal</button>
      </div>
    );
  };

  export default Modal;
