import { useState, useEffect } from "react"
import { ChevronLeft, ChevronRight } from "react-feather"
import { ProjectsData } from "./ProjectsData"

export default function Carousel({
  autoSlide = false,
  autoSlideInterval = 3000,
  projectIndex
}) {
  const [curr, setCurr] = useState(0)

  const prev = () => {
    var videosSize = ProjectsData[projectIndex].ModalData.VideosToShow.length
    var imagesSize = ProjectsData[projectIndex].ModalData.ImagesToShow.length
    
    setCurr((curr) => (
      curr === 0 ? ( Math.max(0,videosSize-1) + Math.max(0,imagesSize-1)) : curr - 1))
    }

  const next = () =>{
    var videosSize = ProjectsData[projectIndex].ModalData.VideosToShow.length
    var imagesSize = ProjectsData[projectIndex].ModalData.ImagesToShow.length

    setCurr((curr) => (curr === Math.max(0,videosSize-1) + Math.max(0,imagesSize-1) ? 0 : curr + 1))
  }
    
  useEffect(() => {
    if (!autoSlide) return
    const slideInterval = setInterval(next, autoSlideInterval)
    return () => clearInterval(slideInterval)
  }, [])

  
  return (
    <div className="relative h-72 w-full md:w-1/2 overflow-hidden rounded-lg bg-base-300">
        <div
        className={`flex h-full place-content-center`}
        >

{/* <iframe
                    title={project.title}
                    class="h-72 w-full md:w-1/2"
                    src={project.trailerUrl}
                    frameBorder="0"
                    allowFullScreen
                  ></iframe> */}


            <img src={ProjectsData[projectIndex].ModalData.ImagesToShow[curr]} className=" object-cover group-hover:opacity-100" />
        </div>
        {/* Button left */}
        <div className="absolute inset-0 flex items-center justify-between p-4">
            <button
            onClick={prev}
            className="p-1 rounded-full shadow bg-white/80 text-gray-800 hover:bg-white"
            >
            <ChevronLeft size={40} />
            </button>
            {/* Button right */}
            <button
            onClick={next}
            className="p-1 rounded-full shadow bg-white/80 text-gray-800 hover:bg-white"
            >
                <ChevronRight size={40} />
            </button>
        </div>
         {/* Lower indicators for which slide is currently being displayed */}
        <div className="absolute bottom-4 right-0 left-0">
            <div className="flex items-center justify-center gap-2">
            {ProjectsData[projectIndex].ModalData.ImagesToShow.map((_, i) => (
                <div
                className={`
                transition-all w-3 h-3 bg-white rounded-full
                ${curr === i ? "p-2" : "bg-opacity-50"}
                `}
                />
            ))}
            </div>
        </div>
    </div>
  )
}
