// Modal.js

import React from 'react';
import Modal from 'react-modal';

// Make sure to bind the modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#root');

const customStyles = {
    content: {
      top: 'calc(50% + 50px)', // Adjust the top property based on your header height
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      border: '1px solid #ccc',
      borderRadius: '2xl',
      boxShadow: 'xl',
      padding: '4',
      maxWidth: '80%',
      width: 'full',
      backgroundColor: '#1a202c', // Dark-ish background color
      color: 'white', // Text color
    },
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0.3)',
    },
  };
  

const CustomModal = ({ isOpen, closeModal, project }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={closeModal}
      contentLabel="Project Modal"
      style={customStyles}
    >
      <h2 className="text-lg leading-7">Project Details</h2>
      <div className="mt-2">
        <img
          src={project.imageSrc}
          alt={project.name}
          className="h-full w-full object-cover object-center rounded-md"
        />
      </div>
      <div className="mt-4 p-4">
        <h3 className="text-lg font-bold">
          <a href={project.href}>{project.name}</a>
        </h3>
        <p className="mt-1 mb-5 text-sm">{project.description}</p>
        <p className="text-sm font-medium">{project.used}</p>
      </div>
      <button
        onClick={closeModal}
        className="absolute top-4 right-4 text-base font-medium text-gray-500 hover:text-gray-800"
      >
        Close
      </button>
    </Modal>
  );
};

export default CustomModal;
