import { useEffect } from "react";
import {
  MapPinIcon,
  AtSymbolIcon,
  CalendarIcon,
  AcademicCapIcon,
  BriefcaseIcon,
} from "@heroicons/react/20/solid";
import AOS from "aos";
import "aos/dist/aos.css";
const features = [
  {
    name: "Age:",
    description: "23 years old",
    icon: CalendarIcon,
  },
  {
    name: "Email:",
    description: "czepieldavid@gmail.com",
    icon: AtSymbolIcon,
  },
  {
    name: "Location:",
    description: "Madrid, Spain",
    icon: MapPinIcon,
  },
];

export default function About() {
  useEffect(() => {
    AOS.init({ duration: 2000 });
  }, []);
  return (
    <div
      className="relative isolate overflow-hidden  px-6 py-24 sm:py-32 lg:overflow-visible lg:px-0"
      id="about"
    >
      <div className="mx-auto grid max-w-2xl grid-cols-1 gap-x-8 gap-y-16 lg:mx-0 lg:max-w-none lg:grid-cols-2 lg:items-start lg:gap-y-10">
        <div className="lg:col-span-2 lg:col-start-1 lg:row-start-1 lg:mx-auto lg:grid lg:w-full lg:max-w-7xl lg:grid-cols-2 lg:gap-x-8 lg:px-8">
          <div className="lg:pr-4">
            <div className="lg:max-w-lg">
              <h2 className="text-lg leading-7">Get to know more</h2>
              <p className="mt-2 text-4xl font-bold tracking-tight sm:text-6xl">
                About Me
              </p>
              <p className="mt-6 text-lg leading-8" data-aos="fade-right">
              Nothing beats the joy you get when you see somebody having fun with a game you've developed. It's this passion that has led me to pursue a career in the world of game development.
Within this vast and dynamic field, I've found my niche in the technical realm, where I delve into the intricacies of algorithms and software architecture principles that bring gaming worlds to life. My dedication to staying abreast of industry standards and emerging technologies is unwavering, as I prioritize continuous learning to sharpen my skills and adapt to the ever-evolving landscape.

While I devote myself to personal projects, I also relish the opportunity to collaborate and test my skills in real-world scenarios, often participating in game jams alongside teams of fellow professionals. These experiences not only challenge me to push my creative boundaries but also foster a spirit of camaraderie and innovation.

Ultimately, my goal is to make meaningful contributions to the gaming industry, crafting experiences that resonate with players and leave a lasting impact on the gaming landscape.             </p>
            </div>
          </div>
        </div>
        <div
          className="-ml-12 -mt-12 p-12 lg:col-start-2 lg:row-span-2 lg:row-start-1 lg:overflow-hidden"
          data-aos="fade-left"
        >
          <img
            className="w-[38rem] ring-2 ring-base-300 max-w-none rounded-xl shadow-xl sm:w-[57rem]"
            src="https://fastly.picsum.photos/id/473/5000/3333.jpg?hmac=OrrE0aUqLbLCJ9PAlSn0dIRHjOnuuuev9IjuO3PGZf8"
            alt="Person"
          />
        </div>
      </div>
    </div>
  );
}
